mapper.update:
UPDATE PAYMENT
SET LIKE_CNT = LIKE_CNT + 1
WHERE PAYMENT_ID = #{paymentId}

UPDATE PAYMENT
SET LIKE_CNT = LIKE_CNT - 1
WHERE PAYMENT_ID = #{paymentId}

mapper.delete:
DELETE FROM BOARD_LIKE
WHERE PAYMENT_ID = #{paymentId}
AND ID = #{id}

mapper.select:
SET @TEST_ROWNUM := 0;
SELECT *,
	@TEST_ROWNUM:=@TEST_ROWNUM+1 AS TEST_ROWNUM
FROM TEST_BOARD
ORDER BY TEST_NUM

mapper.update:
UPDATE TEST_BOARD
SET TEST_TITLE = #{testTitle},
TEST_CONTENT = #{testContent},
FILE_NAME = #{fileName]
WHERE ID = #{id}
AND TEST_NUM = #{testNum}
