mapper.select:
SELECT *, GROUP_NUM AS groupNum, GROUP_ORDER AS groupOrder
FROM AUTO_COMMENT
WHERE PAYMENT_ID = #{paymentId}
ORDER BY
GROUP_NUM,GROUP_ORDER,SEQ
LIMIT #{limit}

SELECT COUNT(*)
FROM AUTO_COMMENT
WHERE PAYMENT_ID = #{paymentId}

mapper.update:
UPDATE AUTO_COMMENT
SET GROUP_NUM = #{seq}
WHERE SEQ = #{seq}

UPDATE AUTO_COMMENT
SET GROUP_ORDER = GROUP_ORDER + 1
WHERE GROUP_NUM = #{groupNum}
AND SEQ != #{seq}
AND GROUP_ORDER >= #{groupOrder}

mapper.select:
SELECT IFNULL(MAX(GROUP_ORDER), 0)
FROM AUTO_COMMENT
WHERE PAYMENT_ID = #{paymentId}
AND GROUP_NUM = #{groupNum}
AND REF_SEQ = #{seq}

mapper.update:
UPDATE AUTO_COMMENT
	SET COMMENT = #{comment}
WHERE SEQ = #{seq}
